# Update with mixed data types
def test_update_with_mixed_data_types():
    # Arrange
    counter = CategoryCounter()
    data = ['apple', 1, 2.5, 1, 1]

    # Act & Assert
    # with pytest.raises(TypeError):
    counter.update(data)

    assert counter.total == Counter({'apple': 1, 1: 3, 2.5: 1})


