# Check behavior with nested data structures
def test_nested_data_structures_behavior():
    # Arrange
    counter = CategoryCounter()
    data = [{'name': 'apple'}, {'name': 'banana'}, {'name': 'apple'}]

    # Act & Assert
    with pytest.raises(TypeError):
        counter.update(data)


